from django.urls import pathfrom .views import (    DashboardView,    UserPasswordResetView, UserListView, UserUpdateView, UserDeleteView, UserDetailView, UserCreateView,    CategoryListView, CategoryCreateView, CategoryUpdateView, CategoryDeleteView,    PostCategoryListView, PostCategoryCreateView, PostCategoryUpdateView, PostCategoryDeleteView,    ProductListView,)app_name = 'admins'urlpatterns = [    path('', DashboardView.as_view(), name='dashboard'),]urlpatterns += [    path('user/', UserListView.as_view(), name='user-list'),    path('user/add/', UserCreateView.as_view(), name='user-add'),    path('user/<int:pk>/', UserDetailView.as_view(), name='user-detail'),    path('user/<int:pk>/change/', UserUpdateView.as_view(), name='user-update'),    path('user/<int:pk>/delete/', UserDeleteView.as_view(), name='user-delete'),    path('user/<int:pk>/reset/password/', UserPasswordResetView.as_view(), name='user-password-reset'),]urlpatterns += [    path('category/', CategoryListView.as_view(), name='category-list'),    path('category/add/', CategoryCreateView.as_view(), name='category-add'),    path('category/<int:pk>/change/', CategoryUpdateView.as_view(), name='category-update'),    path('category/<int:pk>/delete/', CategoryDeleteView.as_view(), name='category-delete'),    path('post-category/', PostCategoryListView.as_view(), name='post-category-list'),    path('post-category/add/', PostCategoryCreateView.as_view(), name='post-category-add'),    path('post-category/<int:pk>/change/', PostCategoryUpdateView.as_view(), name='post-category-update'),    path('post-category/<int:pk>/delete/', PostCategoryDeleteView.as_view(), name='post-category-delete'),    path('product/', ProductListView.as_view(), name='product-list'),]